mapper.select:
SELECT CD_NO, LOG_MSG_SEQ, RCV_NAME, RCV_MOB, SEND_ERROR, SEND_TIME, CAMP_NO FROM CAMPAIGN_DETAIL WHERE CAMP_NO = #{camp_no}
AND (RCV_NAME like CONCAT('%', #{word}, '%') OR RCV_MOB like CONCAT('%', #{word}, '%') )
ORDER BY SEND_ERROR DESC ORDER BY SEND_TIME DESC
LIMIT #{startIndex}, #{pageSize}

SELECT count(CD_NO) FROM CAMPAIGN_DETAIL WHERE CAMP_NO = #{camp_no}
AND (RCV_NAME like CONCAT('%', #{word}, '%') OR RCV_MOB like CONCAT('%', #{word}, '%') )
ORDER BY SEND_ERROR DESC ORDER BY SEND_TIME DESC

SELECT count(CD_NO)
FROM CAMPAIGN_DETAIL
WHERE CAMP_NO=#{camp_no} AND SEND_ERROR=#{error_type}
