mapper.insert:
INSERT INTO PUSH_CAMPAIGN_DETAIL_CLICK (tb_click_id, link_seq, link, msg_push_type, click_cnt, reg_date
, upt_date)
)
VALUES (${tb_click_id}, ${link_seq}, '${link}', '${msg_push_type}', ${click_cnt}, '${reg_date}'
, '${upt_date}')
)

mapper.select:
SELECT cd_id, b.reg_date, reqUid, rtn_type, res_cd, mobile, camp_id, push_log_id 
FROM PUSH_CAMPAIGN_DETAIL A JOIN APP_USER B ON A.user_id = B.user_id
WHERE camp_id=${camp_id}
